import React, { useState } from "react";
import "./Login.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();
  const [user, setUser] = useState({
    username: "",
    password: "",
  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user, //spread operator
      [name]: value,
    });
  };

  const login = () => {
    axios.post("http://localhost:3000/login", user).then((res) => {
      });
  };

  // function handleSubmit(event) {
  //onSubmit={handleSubmit}
  //     event.preventDefault();

  //     // TODO: Submit the registration form data to the server
  //   // clear up the field after submission
  //     setUser({ username: "", userRole: "", password: "" });
  //   }

  // const [name, setName] = useState("");
  // const [password, setPassword] = useState("");
  return (
    <div>
      <form className="formDisplay">
        <h2>Login To Use Soverify solution</h2>
        <h3>You are One Step Away to Use Soverify</h3>

        <div className="mb-3">
          <label htmlFor="exampleInputUserName" className="form-label">
            Name
          </label>
          <input
            type="text"
            className="form-control"
            id="exampleInputUserName"
            aria-describedby="nameLabel"
            name="username"
            value={user.username}
            onChange={handleChange}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="exampleInputPassword" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword"
            aria-describedby="password"
            name="password"
            value={user.password}
            onChange={handleChange}
          />
        </div>

        <button type="submit" className="btn btn-primary" onClick={login}>
          Login
        </button>
      </form>

      <h4 style={{ textAlign: "center" }}>
        if you don't have an account{" "}
        <a href="/register" className="link-light">
          Register here
        </a>
      </h4>
    </div>
  );
}

export default Login;
