import React, { useState } from "react";
import "../boosted/css/boosted.min.css";
//import "../boosted/js/boosted.bundle.min.js";
import axios from "axios";
import { Modal } from "react-bootstrap";
import Popup from "reactjs-popup";

function CreateInvitation() {
  const [did, setDid] = useState("");
  // const [msg, setMsg] = useState("");
  const [invitation, setInvitation] = useState("");

  const handleChange = (e) => {
    setDid(e.target.value);
  };

  function handleSendInvitation(e) {
    e.preventDefault();
    const createInvitation = async () => {
    try {
      var config = {
        method: "post",
        url: "http://localhost:3007/create-invitation",
        data: JSON.stringify({}),
      };
      const response = await axios(config);
      console.log(response);
      setInvitation(response);
    } catch (error) {
      console.error(error);
    }
  }
    // clear field
    setDid("");
    onClose();
  }

  const [showModal, setShowModal] = useState(false);
  let onClose = () => {
    setShowModal(false);
  };
  return (
    <div>
      <h1>Welcome To Soverify Solution</h1>
      {/* <button
        className="btn btn-primary"
        onClick={() => {
          var config = {
            method: "post",
            url: "http://localhost:8021/connections/create-invitation",
            data: JSON.stringify({}),
          };
          axios(config).then(function (response) {
            console.log(response);
            setInvitation(response);

          });
        }}
      >
        Create Invitation
      </button> */}
      <button
        type="button"
        className="btn btn-primary"
        data-toggle="modal"
        onClick={() => {
          setShowModal(true);
        }}
      >
        Send an Invitation to a Branch Agent
      </button>

      {showModal && (
        <Modal fullscreen="true" show={showModal} onHide={onClose} centered>
          <Popup
            trigger={
              <div
                id="exampleModal"
                role="dialog"
                aria-labelledby="exampleModalLabel"
                aria-hidden="true"
                aria-modal="true"
              >
                <div className="modal-dialog" role="document">
                  <div className="modal-content">
                    <div className="modal-header">
                      <h5 className="modal-title" id="exampleModalLabel">
                        Send an Invitation to a Branch to Connect
                      </h5>
                      <button
                        type="button"
                        className="close"
                        onClick={() => {
                          onClose();
                        }}
                        data-dismiss="modal"
                        aria-label="Close"
                      >
                        <span aria-hidden="true">&times;</span>
                      </button>
                    </div>
                    <div className="modal-body">
                      <form>
                        <div className="form-group">
                          <label
                            htmlFor="recipient-name"
                            className="col-form-label"
                          >
                            Recipient DID:
                          </label>
                          <input
                            type="text"
                            className="form-control"
                            id="recipient-name"
                            name="holder-did"
                            onChange={handleChange}
                            value={did}
                          />
                        </div>
                        {/* <div className="form-group">
                          <label
                            htmlFor="message-text"
                            className="col-form-label"
                          >
                            Message:
                          </label>
                          <textarea
                            className="form-control"
                            id="message-text"
                            defaultValue={""}
                            name="holder-msg"
                            onChange={handleChange}
                            value={msg}
                          />
                        </div> */}
                      </form>
                    </div>
                    <div className="modal-footer">
                      <button
                        type="button"
                        className="btn btn-secondary"
                        data-dismiss="modal"
                        onClick={() => {
                          onClose();
                        }}
                      >
                        Close
                      </button>
                      <button
                        type="button"
                        className="btn btn-primary"
                        onClick={handleSendInvitation}
                      >
                        Send Invitation
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            }
          ></Popup>
        </Modal>
      )}
    </div>
  );
}

export default CreateInvitation;
