import React, { useState } from "react";
// import { useParams } from "react-router-dom";

function CreateSchema() {
  // const { did } = useParams();
  const [showModal, setShowModal] = useState(false);
  let onClose = () => {
    setShowModal(false);
  };
  return (
    <div>
      <h2 style={{ padding: "20px" }}>
        Hello Issuer, in order to legitimize this branch you need to follow
        these steps:
      </h2>
      <ul>
        <li>
          <b>Step 1:</b> Click on <i>Create Schema</i> to create and send a form
          to the branch &nbsp;
          <button type="button" className="btn btn-light">
            Create
          </button>
        </li>
        <li>
          <b>Step 2:</b> Wait for the branch to fill in the form
        </li>
        <li>
          <b>Step 3:</b> generate and send a QR code for the Legitimized branch
        </li>
      </ul>
    </div>
  );
}

export default CreateSchema;
