import axios from "axios";
import React, { useEffect, useState } from "react";

function ViewInvitationHolder() {
  const [invitations, setInvitations] = useState([]);

  useEffect(() => {
    // Fetch data from the backend server
    axios
      .get("http://localhost:3007/view-invitation-holder")
      .then((response) => {
        //setInvitations(response.data);
        const data = response.data;
        if (Array.isArray(data)) {
          setInvitations(data);
        } else {
          console.error("Invitations data is not an array:", data);
        }
      })
      .catch((error) => {
        console.error("Failed to fetch invitations:", error);
      });
  }, []);
  
  return (
    <div className="container">
      <h2 style={{ padding: "20px" }}>
        Hello Holder, here are the invitations you received
      </h2>
      <p>
        selon le DID we do a .map() to retrieve data from the database selon
        DID, we show every holder the invitations sent to them
      </p>
    </div>
  );
}

export default ViewInvitationHolder;
