import React, { useState } from 'react'
import "./Login.css"
import axios from 'axios';
import {useHistory} from "react-router-dom"

export default function Login({setLoginUser}) {

  const history = useHistory()
    const [user,setUser] = useState({
        username:"",
        password: ""
    })
    const handleChange = e =>{
    const {name,value} = e.target
    setUser({
    ...user,//spread operator 
    [name]:value
    })
    }

    const login =()=>{
      axios.post("http://localhost:6969/Login",user)
      .then(res=>{alert(res.data.message)
      setLoginUser(res.data.user)
  history.push("/")})
  }

  

function handleSubmit(event) {
    event.preventDefault();

    // TODO: Submit the registration form data to the server
  }

  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  return (
    <div>
      <form className="formDisplay" onSubmit={handleSubmit}>
        <h2>Login To Use Soverify solution</h2>
        <h3>You are One Step Away to Use Soverify</h3>

        <div className="mb-3">
          <label htmlFor="exampleInputUserName" className="form-label">
            Name
          </label>
          <input
            type="text"
            className="form-control"
            id="exampleInputUserName"
            aria-describedby="nameLabel"
            value={name}
            onChange={(event) => setName(event.target.value)}
          />
        </div>


        <div className="mb-3">
          <label htmlFor="exampleInputPassword" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword"
            aria-describedby="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
        </div>


        <button type="submit" className="btn btn-primary" >
          Login
        </button>
      </form>
      
      <h4 style={{textAlign: 'center'}}>if you don't have an account <a href="/register" class="link-light">Register here</a></h4>
    </div>
  )
}
