import axios from "axios";
// import { Modal } from "react-bootstrap";
// import Popup from "reactjs-popup";

function IssueCredential() {

  const handleCreateSchema = () => {
    // Make an API request to create a schema
    axios
      .post("http://localhost:3007/create-schema", {
        name_of_schema: "Branch Legitimate Identity",
      })
      .then((response) => {
        // Handle the response
        console.log("Schema created:", response.data);
      })
      .catch((error) => {
        console.error("Failed to create schema:", error);
      });
  };

  return (
    <div className="container">
      <h2 style={{ padding: "20px" }}>
        Hello Issuer, in order to legitimize this branch you need to follow
        these steps to issue the Verifiable Credential:
      </h2>
      {/* <ul>
