/*Verifiable Credentials JS Library (@digitalbazaar/vc):
A Javascript library for issuing and verifying Verifiable Credentials.
npm i @digitalbazaar/vc

linking the UI with the DID registartion

Ed25519Signature2020 suite (@digitalbazaar/ed25519-signature-2020): 
Ed25519Signature2020 Linked Data Proof suite for use with jsonld-signatures.
npm install @digitalbazaar/ed25519-signature-2020

the package for the revocation list 
npm install @digitalbazaar/vc-status-rl-2020 => it says that it is derecated, so we need to find alternatives


Selective DID Resolver Client (@digitalbazaar/did-io):
A DID (Decentralized Identifier) resolution library for Javascript.
npm i @digitalbazaar/did-io

A DID (Decentralized Identifier) context library for JavaScript:
This project packages the DID Context from the DID specification for use with Node.js and web apps.
npm install did-context

*/

import React, { useState } from "react";
// libraries importation

//import { Ed25519Signature2020 } from "@digitalbazaar/ed25519-signature-2020";
// import { issue, verify } from "@digitalbazaar/vc";
// import { createRevocationList } from "@digitalbazaar/vc-revocation-list";
// import { contexts } from "@digitalbazaar/did-context";
// import { Ed25519VerificationKey2018 } from "@digitalbazaar/ed25519-verification-key-2018";
// import { Ed25519KeyPair } from "@digitalbazaar/ed25519-key-pair";
// import { didFromKey } from "@digitalbazaar/did-io";

// public and private key creation
// const crypto = require('crypto');

// //Generate a new key pair
// const { publicKey, privateKey } = crypto.generateKeyPairSync('ed25519', {
//   // Recommended key length for ed25519 is 256 bits (32 bytes)
//   modulusLength: 256,
// });

// // Export the keys in PEM format
// const publicKeyPem = publicKey.export({
//   type: 'spki',
//   format: 'pem',
// }).toString();
// const privateKeyPem = privateKey.export({
//   type: 'pkcs8',
//   format: 'pem',
// }).toString();

// // Store the keys somewhere secure
// // For simplicity, we'll just log them to the console
// console.log('Public key:', publicKeyPem);
// console.log('Private key:', privateKeyPem);

function Register() {
  // const [name, setName] = useState("");
  // const [role, setRole] = useState("");
  // const [password, setPassword] = useState("");

  const [user,setUser] = useState({
    username:"",
    userRole:"",
    password: ""
})

const handleChange = e =>{
  // e.preventDefault();
  const {name,value} = e.target
  setUser({
  ...user,//spread operator used because we put the user state as an object, so we need to spread it to ba able to use it: e.g. user.username, user.password
  [name]:value
  })
  }

  function handleSubmit(e) {
    e.preventDefault();

    // TODO: Submit the registration form data to the server

    // clear up the field after submission
    
    setUser("")
  }

  return (
    <div>
      <form className="formDisplay" onSubmit={handleSubmit}>
        <h2>Register To Use Soverify solution</h2>
        <h3>Get a Hold on Your DID to Use Soverify</h3>

        <div className="mb-3">
          <label htmlFor="exampleInputUserName" className="form-label">
            Name
          </label>
          <input
            type="text"
            className="form-control"
            id="exampleInputUserName"
            aria-describedby="nameLabel"
            name="username"
            value={user.username}
            onChange={handleChange}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="exampleInputUserRole" className="form-label">
            User Role
          </label>
          {/* <input
            type="text"
            className="form-control"
            id="exampleInputUserRole"
            aria-describedby="userRole"
            value={role}
            onChange={(event) => setRole(event.target.value)}
          /> */}
          <select
            className="form-select"
            aria-label=".form-select-sm example"
            name="userRole"
            onChange={handleChange}
          >
            <option selected>Open this to select your role</option>
            <option value={user.userRole}>Issuer</option>
            <option value={user.userRole}>Holder</option>
          </select>
        </div>

        <div className="mb-3">
          <label htmlFor="exampleInputPassword" className="form-label">
            Password
          </label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword"
            aria-describedby="password"
            name="password"
            value={user.password}
            onChange={handleChange}
          />
        </div>

        <button type="submit" className="btn btn-primary">
          Register Now
        </button>
      </form>
    </div>
  );
}

export default Register;
