import React from "react";
import axios from "axios";
// import { Modal } from "react-bootstrap";
// import Popup from "reactjs-popup";

function IssueCredential() {

  const handleCreateSchema = () => {
    // Make an API request to create a schema
    axios
      .post("http://localhost:3007/create-schema", {
        name_of_schema: "Branch Legitimate Identity",
      })
      .then((response) => {
        // Handle the response
        console.log("Schema created:", response.data);
      })
      .catch((error) => {
        console.error("Failed to create schema:", error);
      });
  };

  return (
    <div className="container">
      <h2 style={{ padding: "20px" }}>
        Hello Issuer, in order to legitimize this branch you need to follow
        these steps to issue the Verifiable Credential:
      </h2>
      <ul>
        <li>
          <b>Step 1:</b> Click on <i>Create Schema</i> to define the structure
          of the Verifiable Credential you want to issue &nbsp;
        </li>
        <li>
          <b>Step 2:</b> Create the Credential Definition for the Specific
          Schema: this will allow you to create the necessary cryptographic keys
          for a secure information.
        </li>
        <li>
          <b>Step 3:</b> Issue Credential and Send it to the Holder which is the
          Branch you want to legitmize
        </li>
      </ul>

      <div className="container">
        <div
          className="w3-container w3-card w3-padding w3-margin"
          style={{ float: "left", width: "31%" }}
        >
          <h3>1. Create Schema:</h3>
          <form method="POST">
            <label>Name of Schema: </label>
            <input
              type="text"
              className="w3-input"
              name="name_of_schema"
              defaultValue="Branch Legitimate Identity"
            />
            <br />
            <br />
            <label>Schema Version: </label>
            <input
              type="text"
              className="w3-input"
              name="version"
              defaultValue="1.0"
            />
            <br />
            <br />
            <label>Please supply a JSON array of attributes:</label>
            <br />
            <br />
            <textarea
              style={{ height: 100 }}
              className="w3-input"
              name="attributes"
              required
              defaultValue={
                '[\n    "BranchName",\n    "BranchManager",\n    "Location"\n ]'
              }
            />
            <br />
            <br />
            <button
              type="button"
              className="btn btn-light"
              onClick={handleCreateSchema}
            >
              Submit
            </button>
          </form>
        </div>
      </div>

      
    </div>
  );
}

export default IssueCredential;
